import React, { Component } from 'react';
const todoItems = [
  {
    "id": 1,
    "title": "Learn React!",
    "description": "suck it up and learn react my dude",
    "completed": false
  },
  {
    "id": 2,
    "title": "CONVINCE CHRIS",
    "description": "Figure out a way to convince the mokies that django/react is the way...",
    "completed": false
  },
  {
    "id": 3,
    "title": "REDUX",
    "description": "Prep yourself to get mentally \"wrekt\" according to the boi",
    "completed": false
  },
  {
    "id": 4,
    "title": "New todo",
    "description": "random stuffs",
    "completed": false
  }
]; 
class App extends Component {
  constructor(props) {
      super(props);
      this.state = {
        viewCompleted: false,
        todoList: todoItems
      }
  };

displayCompleted = status => {
  if (status){
    return this.setState({viewCompleted: true});
  }
  return this.setState({ viewCompleted: false });
}
renderTabList = () => {
  return(
    <div className="my-5 tab-list">
      <span
        onClick={() => this.displayCompleted(true)}
        className={this.state.viewCompleted ? "active" : ""}
      >
        complete
      </span>
      <span
        onClick={() => this.displayCompleted(false)}
        className={this.state.viewCompleted ? "" : "active"}
      > 
        Incomplete 
      </span>
    </div>
  );
};
renderItems = () => {
  const { viewCompleted } = this.state;
  const newItems = this.state.todoList.filter (
    item => item.completed == viewCompleted
  );
  return newItems.map(item => (
    <li 
      key={item.id} 
      className="list-group-item d-flex justify-content-between align-items-center"
    >
      <span
        className={`todo-title mr-2 ${
          this.state.viewCompleted ? "completed-todo" : ""
        }`}
        title ={item.description}
        >
          {item.title}
        </span>
      <span>
        <button className="btn btn-secondary mr-2"> Edit </button>
        <button className="btn btn-danger">Delete </button>
      </span>
    </li>
  ));
};
  render() {
    return (
      <main className="content">
        <h1 className="text-white text-uppercase text-center my-4">Todo app</h1>
        <div className="row ">
          <div className="col-md-6 col-sm-10 mx auto p-0">
            <div className="card p-3">
              <div className="">
                <button className="btn btn-primary">Add task</button>
              </div>
                {this.renderTabList()}
              <ul className="list-group list-group-flush">
                {this.renderItems()}
                </ul>
              </div>
            </div>
          </div>
        </main>
    );
  }
}   

export default App;
